class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> mp;
        vector<int> temp;
        int n = nums.size();

        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int x = max({a, b, k - a, k - b});
            mp[diff].push_back(x);
            temp.push_back(x);
        }

        sort(temp.begin(), temp.end());
        int ans = n;

        for (auto& it : mp) {
            int diff = it.first;
            int ele = lower_bound(temp.begin(), temp.end(), diff) - temp.begin();
            ele = ele*2+(nums.size()/2-ele);


            for (auto& x : it.second) {
                if (x < diff) {
                    ele -= 2;
                } else {
                    ele -= 1;
                }
            }

            ans = min(ans, ele);
        }

        return ans;
    }
};
