class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int>x1;
        int n= a.size();
        unordered_map<int,int>mp;
        for(int i = 0;i<n/2;i++){
            x1.push_back(max(max(a[i],a[n-i-1]),k-min(a[i],a[n-i-1])));
            mp[abs(a[i]-a[n-i-1])]++;
        }
        int ans = INT_MAX;
        sort(x1.begin(),x1.end());
        for(int i = 0;i<=k;i++){
            int res = n;
            res-=2*mp[i];
            auto u = upper_bound(x1.begin(),x1.end(),i-1);
            int u1 = u-x1.begin();
            res-=((n/2)-u1-mp[i]);
            ans = min(ans,res);
        }
        return ans;
    }
};
