class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;

        vector<int>v;

        int n=nums.size();

        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int candidate = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[diff].push_back(candidate);
            v.push_back(candidate);
        }

        int res = n;
        sort(v.begin(),v.end());

        for(auto x:mp){
            int pivot = lower_bound(v.begin(),v.end(),x.first)-v.begin();
            pivot = pivot*2 + (n/2-pivot);

            for(auto y:x.second){
                if(y<x.first){
                    pivot-=2;
                }
                else{
                    pivot-=1;
                }
            }

            res = min(res,pivot);
        }

        return res;
    }
};
