class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n=len(nums)
        m=defaultdict(list)
        v=[]
        for i in range(n//2):
            a,b=nums[i],nums[n-i-1]
            diff=abs(a-b)
            x=max(a,b,k-a,k-b)
            m[diff].append(x)
            v.append(x)
        res=n
        v.sort()
        for key,val in m.items():
            T=bisect.bisect_left(v,key)
            T=T*2+(n//2-T)
            for x in val:
                if x<key:
                    T-=2
                else:
                    T-=1
            res=min(res, T)
        return res
