class Solution {
public:
    int minChanges(vector<int>nums,int k) {
	 map<int,vector<int>> mp;
        vector<int> arr;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int nearest = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[diff].push_back(nearest);
            arr.push_back(nearest);
        }
        int ans = nums.size();
        sort(arr.begin(),arr.end());

        for(auto x:mp){

            int diff = x.first;
            int least = lower_bound(arr.begin(),arr.end(),diff)-arr.begin();
            int temp=least;
            least = least*2;
            least+=n/2-temp;
            for(auto y:x.second){
                if(y<diff){
                    least-=2;
                }
                else{
                    least-=1;
                }
            }
            ans = min(ans,least);
        }
        return ans;
}
};
