class Solution {
public:
    static bool comp(pair<int, int> f, pair<int, int> s) {

        if (f.second == s.second) {
            return f.first < s.first;
        }

        if (f.second > s.second) {
            return true;
        } 
        return false;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> mp;
        for(int i = 0; i < n /2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            mp[diff]++;
        }

        // int ele = -1, mn = -1;
        vector<pair<int, int> > pv;
        for(auto a: mp) {
            pv.push_back({a.first, a.second});
        }
        sort(pv.begin(), pv.end(), comp);
        for(int i=0; i<pv.size(); i++) {
            cout<<pv[i].first << " "<<pv[i].second<<endl;
        }
        vector<int> fv;
        int st = 0;
        while(st < pv.size()) {
            int j = st;
            fv.push_back(pv[st].first);
            while (j < pv.size() and pv[j].second == pv[st].second) {
                j ++;
            }
            st = j;
        }
        for(int i=0; i<fv.size(); i++) cout<<fv[i] << " ";
        cout<<endl;
        int fa = INT_MAX;
        for(int ev: fv) {
            int ans = 0;
            for(int i=0; i<n/2; i++) {
                int diff = abs(nums[i] - nums[n - i - 1]);
                if (diff > ev) {
                    ans ++;
                } else if (diff < ev) {
                    int x = min(nums[i], nums[n - i - 1]), y = max(nums[i], nums[n - i - 1]);
                    int rmdiff = ev - diff;
                    if (x >= rmdiff) {
                        ans ++;
                    } else if (rmdiff <= k - y) {
                        ans ++;
                    } else {
                        ans += 2;
                    }
                }
            }
            fa = min(fa, ans);
        }

        return fa;
    }
};
