class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> s;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int di = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[di].push_back(X);
            s.push_back(X);
        }
        int sol = nums.size();
        sort(s.begin(),s.end());  
        for(auto &i:m){
            int di = i.first;
            int T = lower_bound(s.begin(),s.end(),di)-s.begin();
            T = T*2+(nums.size()/2-T);
            for(auto &X:i.second){
                if(X<di){
                    T-=2;
                }else{
                    T-=1;
                }
            }
            sol = min(sol,T);
        }
        return sol;
    }
};
