class Solution
{
public:
    int minChanges(vector<int> &nums, int k)
    {
        map<int, vector<int>> m;
        vector<int> v;
        int n = nums.size();
        for (int i = 0; i < n / 2; i++)
        {
            int num = max({nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1]});
            m[abs(nums[i] - nums[n - i - 1])].push_back(num);
            v.push_back(num);
        }

        int ans = nums.size();
        sort(v.begin(), v.end());

        for (auto &&ii : m)
        {
            int diff = ii.first;
            int x = lower_bound(v.begin(), v.end(), diff) - v.begin();
            int num = (n / 2 - x);
            x = x * 2 + num;

            for (auto &&it : ii.second)
            {
                if (it < diff)
                {
                    x -= 2;
                }
                else
                {
                    x -= 1;
                }
            }

            ans = min(ans, x);
        }

        return ans;
    }
};
