class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int l = 0, r = n - 1;
        vector<int> change(k + 10, 0);
        while(l < r) {
            int x = max(nums[l], nums[r]);
            int y = min(nums[l], nums[r]);
            int t = x - y;
            int mx = max(x, k - y);
            // [0, t) (t, mx] 1
            // (mx, k] 变 2
            // 向前平移
            // [1, t] [t + 2, mx + 1]
            // [mx + 2, k + 1]
            change[0] ++;
            change[t] --;
            change[t + 1] ++;
            change[mx + 1] --;
            change[mx + 1] += 2;
            change[k + 1] -= 2;
            l ++, r --;
        }
        int res = INT_MAX;
        for(int i = 1; i < k + 2; i ++) change[i] += change[i-1];
        for(int i = 0; i <= k; i ++) res = min(res, change[i]);
        return res;
    }
};
