class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int mini = INT_MAX, n = nums.size();
        map<int,int> m;
        for( int i = 0; i < n/2; i++ ){
            m[abs(nums[i]-nums[n-i-1])]++;
        }
        for( auto p : m ){
            int x = p.first;
            int count = 0;
            if( n/2 - p.second > mini ) continue;
            for( int i = 0; i < n/2 && count < mini; i++ ){
                if(abs(nums[i]-nums[n-i-1]) == x) continue;
                bool once = false;
                for( auto a : {nums[i],nums[n-i-1]}){
                    for( auto b : {x,-x}){
                        if( a + b >= 0 && a + b <= k ){
                            once = true;
                        }
                    }
                }
                if( !once ) count++;
                count++;
            }
            mini = min( count, mini );
        }
        return mini;
    }
};
