class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> max_diff(k + 1, 0), diff(k + 1, 0);
        int n = nums.size();
        for (int i = 0; i < n / 2; i++) {
            int d = max(nums[i], nums[n - i - 1]);
            d = max(d, abs(nums[i] - k));
            d = max(d, abs(nums[n - i - 1] - k));
            max_diff[d]++;
            diff[abs(nums[i] - nums[n - i - 1])]++;
        }
        for (int i = k - 1; i >= 0; i--) {
            max_diff[i] += max_diff[i + 1];
        }
        int res = INT_MAX;
        for (int i = 0; i <= k; i++) {
            res = min(res, n - max_diff[i] - diff[i]);
        }
        return res;
    }
};
