class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;

        int n=nums.size();
        for(int i=0;i<nums.size()/2;i++){
            int a=nums[i],b=nums[n-i-1];
         int val=abs(nums[i]-nums[n-1-i]);
            int x=max({a,b,k-a,k-b});
            mpp[val].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        for(auto &val2:mpp){
     int diff=val2.first;
            int t=lower_bound(v.begin(),v.end(),diff)-v.begin();
            t=t*2+(nums.size()/2-t);
        for(auto &x:val2.second){
if(x<diff){
t-=2;}
            else{
t-=1;}

}
            n=min(n,t);
        }return n;
    }
};
