int a[100020];
int sum[100020];
int sum2[100020];
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int m = n/2;
        memset(sum,0,sizeof(sum));
        memset(sum2,0,sizeof(sum2));
        for(int i=0;i<n/2;++i){
            a[i] = max(max(nums[i], nums[n-i-1]), max(k - nums[i], k - nums[n-i-1]));
            ++ sum[a[i]];
            int x = abs(nums[i] - nums[n-i-1]);
            ++ sum2[x];
        }
        int ans = n;
        for(int i=k;i>=0;--i){
            sum[i] += sum[i+1];
            int x = m - sum[i];
            int y = sum[i] - sum2[i];
            x *= 2;
            ans = min(ans, x+y);

        }

        return ans;
    }
};
