class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = Counter()
        diff = [0] * (k + 2)
        for i in range(n // 2):
            x, y = nums[i], nums[n - i - 1]
            if x > y:
                x, y = y, x
            res = k - min(x, k - y)
            cnt[y - x] += 1
            diff[0] += 1
            diff[res + 1] -= 1

        for i in range(1, k + 1):
            diff[i] += diff[i - 1]


        ans = inf
        for x, v in cnt.items():
            res = diff[x] - v + (n // 2 - diff[x]) * 2
            ans = min(ans, res)
        return ans
