class Solution {
public:
    int minChanges(vector<int>& vansh1, int vansh2) {
        int vansh3 = vansh1.size();
        map<int, vector<int>> vansh4;
        vector<int> vansh5;
        int vansh6 = 0;

        while (vansh6 < vansh3 / 2) {
            vansh4[abs(vansh1[vansh6] - vansh1[vansh3 - vansh6 - 1])].push_back(max({vansh1[vansh6], vansh1[vansh3 - vansh6 - 1], vansh2 - vansh1[vansh6], vansh2 - vansh1[vansh3 - vansh6 - 1]}));
            vansh5.push_back(max({vansh1[vansh6], vansh1[vansh3 - vansh6 - 1], vansh2 - vansh1[vansh6], vansh2 - vansh1[vansh3 - vansh6 - 1]}));
            vansh6++;
        }
        sort(vansh5.rbegin(), vansh5.rend());
        reverse(vansh5.begin(), vansh5.end());
        int vansh7 = vansh3;


        for (auto &vansh8 : vansh4) {
            int vansh9 = lower_bound(vansh5.begin(), vansh5.end(), vansh8.first) - vansh5.begin();
            vansh9 =  vansh3 / 2 + vansh9 * 2 - vansh9;

            for (auto &vansh10 : vansh8.second) {
                if (vansh10 >= vansh8.first) {
                    vansh9 -= 1;
                } else {
                    vansh9 -= 2;
                }
            }

            vansh7 = min(vansh7, vansh9);
        }

        return vansh7;
    }
};
