class Compare {
public:
    bool operator()(pair<int,int> &a, pair<int,int> &b){
        return ((a.second == b.second)?(a.first > b.first):(a.second < b.second));
    }

};

class Solution {
private:
    int solver(vector<int> &nums, int n, int k, int x){
        int ans = 0;
        for(int i = 0; i < (n/2); i++){
            // cout << "i = " << i << endl;
            if(abs(nums[i] - nums[n - 1 - i]) != x){
                int mini = min(nums[i], nums[n-1-i]);
                int maxi = max(nums[i], nums[n-1-i]);
                if((maxi - mini) > x) ans++;
                else if((maxi - mini) < x){
                    if(((x + mini) <= k) || ((maxi - x) >= 0)) ans++;
                    else ans += 2;
                }
            }
            // cout << "ans = " << ans << endl;
        }
        return ans;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        // Frequency
        int n = nums.size();
        unordered_map<int, int> freq;
        for(int i = 0; i < (n/2); i++){
            freq[abs(nums[i] - nums[n - 1 - i])]++;
        }
        priority_queue<pair<int,int>, vector<pair<int, int>>, Compare> pq;
        for(auto it : freq){
            pq.push({it.first, it.second});
        }
        int ans = n;
        int diff = 0;
        while(!pq.empty()){
            auto it = pq.top();
            pq.pop();
            ans = min(ans, solver(nums, n, k, it.first));
            if(diff == 0){
                diff = it.second;
            }
            else if(diff > it.second) break;
        }
        return ans;
    }
};
