class Solution {
public:
    /*
    max(k-nums[i] , nums[i]) , 0


    */
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size() ;
        vector<int>freq(100001,0) , pre(100002), post(100002) ;
        int i = 0 , j = n-1 ;
        while(i < j)
        {
            int mx = max(nums[i],nums[j]) ;
            int mn = min(nums[i],nums[j]) ;
            mx = max(mx,k-mn) ;
            mx = max(mx,mn) ;
            pre[mx]++ ;
            post[mx]++ ;
            freq[max(nums[i],nums[j])-min(nums[i],nums[j])]++ ;
            i++ , j-- ;
        }
        for(int i = 1 ; i <= 100000 ; i++)
        {
            pre[i]+=pre[i-1] ;
        }
        for(int i = 100000 ; i >= 0 ; i--)
            post[i]+=post[i+1] ;
        int ans = INT_MAX ;
        for(int i = 0; i < 100001 ; i++)
        {

            ans = min(ans , (i ? (pre[i-1]*2): 0) +post[i]- freq[i]) ;
          // cout<<pre[i]<<" "<<post[i]<<" "<<freq[i]<<endl ;
        }
        return ans ;
    }
};
