class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        //Initializing a vector
        vector<int> list;
        // making hashmap
        map<int,vector<int>> fill;
        int l = nums.size();
        for(int i = 0 ; i < (l/2) ; i++ ){

            int a = nums[i];
            int b = nums[l-i-1];
            int dif = abs(a-b);
            int maximum =  max({a,b,k-a,k-b});
            fill[dif].push_back(maximum);
            list.push_back(maximum);
        }
        int res = l;
        // sorting the list
        sort(list.begin(),list.end());
        for(auto &val : fill){
            int diff = val.first;
            int d = lower_bound(list.begin(),list.end(),diff)-list.begin();
            d = d*2+(l/2-d);
            for(auto &i : val.second){
                if(i < diff){
                    d -= 2;
                } else {
                    d--;
                }
            }
            res = min(res,d);
        }
    return res;
    }
};
