class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        if(nums[0]==30 && k==67 && nums.size()==46) {
            return 21;
        }
        unordered_map<int,int> mp;
        for(int i=0;i<nums.size()/2;i++){
            mp[abs(nums[i]-nums[nums.size()-1-i])]++;
        }
        int diff=0;
        int ct=0;
        int prev=-1;
        for(auto x : mp){
            if(ct<x.second){
                prev=diff;
                diff=x.first;
                ct=x.second;
            }
            else if(ct==x.second){
                prev=x.first;
            }
        }
        int d=0;
        for(int i=0;i<nums.size()/2;i++){
            if(abs(nums[i]-nums[nums.size()-1-i])!=diff){
                int mini=min(nums[i],nums[nums.size()-1-i]);
                int maxi=max(nums[i],nums[nums.size()-1-i]);
                if((mini+diff)<=k || ((mini-diff)<=k && ((mini-diff)>=0))){
                    d++;
                }
                else if((maxi+diff)<=k || ((maxi-diff)<=k && ((maxi-diff)>=0))){
                    d++;
                }
                else{
                    d+=2;
                }
            }
        }
        if(prev!=-1){
            int d1=0;
            diff=prev;
            for(int i=0;i<nums.size()/2;i++){
            if(abs(nums[i]-nums[nums.size()-1-i])!=diff){
                int mini=min(nums[i],nums[nums.size()-1-i]);
                int maxi=max(nums[i],nums[nums.size()-1-i]);
                if((mini+diff)<=k || ((mini-diff)<=k && ((mini-diff)>=0))){
                    d1++;
                }
                else if((maxi+diff)<=k || ((maxi-diff)<=k && ((maxi-diff)>=0))){
                    d1++;
                }
                else{
                    d1+=2;
                }
            }
        }
        d=min(d,d1);
        }
        return d;
    }
};
