class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int MAXV = 100000;
        int n = nums.size();
        if (n & 1) {
            int ans = 0;
            for (int i = 0, j = n - 1; i < j; ++i, --j) {
                if (nums[i] != nums[j]) ans += 1;
            }
            return ans;
        }

        int m = n / 2;
        for (int i = 0, j = n - 1; i < j; ++i, --j) {
            if (nums[i] > nums[j]) swap(nums[i], nums[j]);
        }

        vector<int> cnt(k + 1);
        vector<int> eq(k + 1);
        for (int i = 0, j = n - 1; i < j; ++i, --j) {
            int lr = max(nums[j], k - nums[i]);
            cnt[lr] += 1;
            eq[nums[j] - nums[i]] += 1;
        }

        for (int i = k - 1; i >= 0; --i) {
            cnt[i] += cnt[i + 1];
        }

        int ans = n;
        for (int limit = 0; limit <= k; ++limit) {
            int c = cnt[limit] - eq[limit];
            ans = min(ans, c + (m - eq[limit] - c) * 2);
        }
        return ans;
    }
};
