class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n = v.size();
        map<int,vector<int>> mp;
        vector<int> maxDif;
        int ans = n;
        for(int i=0;i<n/2;i++){
            int dif = abs(v[i]-v[n-i-1]);
            int maxdif = max({v[i],k-v[i],v[n-i-1],k-v[n-i-1]});
            mp[dif].push_back(maxdif);
            maxDif.push_back(maxdif);
        }
        sort(maxDif.begin(),maxDif.end());
        for(auto [x,y]:mp){
            int ind = lower_bound(maxDif.begin(),maxDif.end(),x)-maxDif.begin();
            int prevEnd = ind + n/2;
            for(auto p:y){
                prevEnd--;
                if(p < x){
                    prevEnd--;
                }
            }
            ans = min(ans,prevEnd);
        }
        return ans;
    }
};
