class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        Map<Integer, Integer> map = new HashMap<>();
        int[] dis = new int[n / 2];
        for (int i = 0; i < n / 2; i++) {
            int key = Math.abs(nums[i] - nums[n - i - 1]);
            if (map.containsKey(key)) {
                map.put(key, map.get(key) + 1);
            } else {
                map.put(key, 1);
            }
            dis[i] = key;
        }
        List<Integer> keyList = new ArrayList<>(map.keySet());
        Collections.sort(keyList, (a, b) -> map.get(b) - map.get(a));
        int answer = Integer.MAX_VALUE/2;
        for (int index = 0; index < 10 && index< keyList.size() ; index++) {
            int key = keyList.get(index);
            boolean can = true;
            for (int i = 0; i < n / 2; i++) {
                if (nums[i] - key > k && nums[n - i - 1] - key > k) {
                    can = false;
                    break;
                }
            }
            if (can) {
                int result = 0;
                for (int i = 0; i < n / 2; i++) {
                    if (dis[i] != key) {
                        int min = Math.min(nums[i], nums[n - i - 1]);
                        int max = Math.max(nums[i], nums[n - i - 1]);
                        if (min + key > k && max - key < 0) {
                            // System.out.println(i);
                            result += 2;
                        } else {
                            result += 1;
                        }
                    }
                }
                answer = Math.min(answer, result);
            }
        }
        return answer;
    }
}
