class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int result=n;
        vector<int>temp;
        //creating a map
        unordered_map<int,vector<int>>m1;
        for(int i = 0;i<n/2;i++){
            int y=nums[n-i-1];
            int x = nums[i];
            int temp2 = max({k-x,k-y,x,y});
            int difference=abs(x-y);
            temp.push_back(temp2);
            m1[difference].push_back(temp2);
        }
        //sorting our vector
        sort(temp.begin(),temp.end());
        for(auto &it:m1){
            //calculating difference
            int difference = it.first;
            //lowerbound
            int lb = lower_bound(temp.begin(),temp.end(),difference)-temp.begin();
            lb= lb*2+(n/2-lb);
            //now iterating in second vector
            for(auto &j:it.second){
                if(j<difference)
                lb=lb-2;
                else
                lb=lb-1;
            }
            result = min(result,lb);
        }

        return result; 
    }
};
