class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> diff;
        vector<int> pos(k+1,0);

        for(int i=0;i<n/2;i++)
        {
            int no1=nums[i];
            int no2=nums[n-i-1];
            diff[abs(nums[i]-nums[n-i-1])]++;
            pos[max(k-min(no1,no2),max(no1,no2))]++;
        }
        for(int i=k-1;i>=0;i--)
        {
            pos[i]+=pos[i+1];
        }
        int ans=INT_MAX;
        for(auto x:diff)
        {
            int dif=x.first;
            int f=x.second;
            int no=(n/2-pos[dif])*2;
            no+=pos[dif]-f;
            ans=min(ans,no);
            //cout<<dif<<" "<<f<<" "<<pos[dif]<<endl;
        }
        return ans;
    }
};
