class Solution {
public:
    int f(vector<int>& nums,int& val,int& k){
        int ans=0;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            if(abs(a-b)!=val){
                if(max(a,abs(a-k))<val && max(b,abs(b-k))<val){
                    ans+=2;
                }
                else{
                    ans++;
                }
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        int n = nums.size();
        int freq=0,val=INT_MAX;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            if(freq<m[abs(nums[i]-nums[n-i-1])]){
                freq= m[abs(nums[i]-nums[n-i-1])];
                val = abs(nums[i]-nums[n-i-1]);
            }
        }
        vector<pair<int,int>> values;
        for(auto i:m){
            values.push_back({i.second,i.first});
        }
        sort(values.begin(),values.end());
        int ans=INT_MAX;
        for(int i=values.size()-1;i>=0;i--){
            int rt = f(nums,values[i].second,k);
            if(rt>ans){
                break;
            }
            else{
                ans = rt;
            }
        }
       return ans;


    }
};
