class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>m;
        vector<int>v;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            v.push_back(max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1])));
        }
        int mini=*min_element(v.begin(),v.end());
        int diff1,diff2;
        int maxi1=0,maxi2=0;
        for(auto it:m){
            if(it.second>maxi1){
                diff1=it.first;
                maxi1=it.second;
            }
            if(it.first<=mini && it.second>maxi2){
                diff2=it.first;
                maxi2=it.second;
            }
        }
        int ans1=0,ans2=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])==diff1){
                continue;
            }
            else if(v[i]<diff1){
                ans1+=2;
            }
            else{
                ans1++;
            }
        }
        ans2=n/2-maxi2;
        return min(ans1,ans2);
    }
};
