class Solution {
public:

    int solve(vector<int>& nums, int diff, int k){
        int n = nums.size(), ans=0;

        for(int i=0; i<n/2; i++){
            if(abs(nums[i] - nums[n-i-1]) == diff) continue;
            else if((nums[i] >= diff) || (nums[n-i-1] >= diff) ) ans += 1;
            else if( min((nums[i]+diff), (nums[n-i-1]+diff)) <= k) ans += 1;
            else ans += 2;
        }

        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        map<int, int> mp;
        int n=nums.size(), mini=INT_MAX;

        for(int i=0; i<n/2; i++){
            mp[abs(nums[i] - nums[n-i-1])]++;
        }

        vector<pair<int,int>> v;

        for(auto it: mp){
            v.push_back({it.second, it.first});
        }

        sort(v.begin(), v.end());

        for(auto it: v){
            if(n/2 - it.first < mini){
                mini = min(mini, solve(nums, it.second, k));
            }
        }


        return mini;
    }
};
