class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> um;
        int n = nums.size(), res = n / 2;
        int f[k + 2];
        memset(f, 0, sizeof f);
        for (int i = 0; i * 2 < n; ++i) {
            int a = nums[n - i - 1], b = nums[i];
            // cout << a << " " << b << endl;
            if (a > b) swap(a, b);
            if (a == b) f[1]++, f[k - min(a, k - b) + 1] ++;
            else {
                f[0]++, f[b - a]--;
                f[b - a + 1]++;
                f[k - min(a, k - b) + 1] ++;
            }
            // cout << f[4] << endl;
        }
        int sum = 0;
        for (int i = 0; i <= k; ++i) sum += f[i], res = min(res, sum);
        return res;
    }
};
