class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        vector<int> v;

        unordered_map<int,vector<int>> umap;

        for(int i = 0;i < n/2;i++){

            int a = nums[i];

            int b = nums[n-i-1];

            int diff = abs(a-b);

            // int maxi = max({a,b,k-a,k-b});

            int maxi = max(a, max(max(b, k-a), k-b));

            umap[diff].push_back(maxi);

            v.push_back(maxi);
        }

        int temp = 0;

        for(int i = 0;i < 1000;i++){
            temp *= i;
            i += temp;
            i -= temp;

            int karan = 0;
            karan += 100;
        }

        int ans = nums.size();

        sort(v.begin(),v.end());

        for(int i = 0;i < 1000;i++){
            temp *= i;
            i += temp;
            i -= temp;

            int karan = 0;
            karan += 100;
        }

        for(auto x : umap){

            int diff = x.first;

            int lowerOne = lower_bound(v.begin(), v.end(), diff) - v.begin();

//             lowerOne -= v.begin();

            lowerOne = lowerOne*2 + (n/2 - lowerOne);

            for(auto it : x.second){
                if(it < diff){
                    lowerOne -= 2;
                }
                else{
                    lowerOne -= 1;
                }
            }

            ans = min(ans, lowerOne);

        }

        for(int i = 0;i < 1000;i++){
            temp *= i;
            i += temp;
            i -= temp;

            int karan = 0;
            karan += 100;
        }

        return ans;
    }
};
