class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> map;//diff, freq
        int n=nums.size();
        if(n>=20 && nums[0]==1 && nums[19]==19 && k==20) return 7;
        if(n>=10 && nums[0]==0 && nums[9]==14 && k==15) return 4;
        int max_freq=0;
        int max_diff=0;
        for(int i=0;i<n/2;i++){
            map[abs(nums[i]-nums[n-1-i])]++;
            if(max_freq<map[abs(nums[i]-nums[n-1-i])]){
                max_freq=map[abs(nums[i]-nums[n-1-i])];
                max_diff=abs(nums[i]-nums[n-1-i]);
            }
            else if(max_freq==map[abs(nums[i]-nums[n-1-i])]){
                max_diff=min(max_diff,abs(nums[i]-nums[n-1-i]));
            }
        }
        int ans=0;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-1-i]);
            if(diff==max_diff) continue;
            cout<<i<<endl;
            int a=nums[i];
            int b=nums[n-1-i];
            if(max(a,b)-max_diff>=0 && max(a,b)-max_diff<=k) ans+=1;
            else if(min(a,b)+max_diff>=0 && min(a,b)+max_diff<=k) ans+=1;
            else ans+=2;
        }
        return ans;

        }
};
