  class UnionFind {
 private:
  std::vector<int> parent;
  std::vector<int> height;
  std::vector<int> m_size;
  int forest_num;

 public:
  UnionFind(int size_) : parent(size_), height(size_, 0), m_size(size_, 1) {
    forest_num = size_;
    for (int i = 0; i < size_; ++i) parent[i] = i;
  }
  void init(int size_) {
    parent.resize(size_);
    height.resize(size_, 0);
    m_size.resize(size_, 1);
    forest_num = size_;
    for (int i = 0; i < size_; ++i) parent[i] = i;
  }
  int find(int x) {
    if (parent[x] == x) return x;
    return parent[x] = find(parent[x]);
  }
  void unite(int x, int y) {
    x = find(x);
    y = find(y);
    if (x == y) return;
    int t = size(x) + size(y);
    m_size[x] = m_size[y] = t;
    if (height[x] < height[y])
      parent[x] = y;
    else
      parent[y] = x;
    if (height[x] == height[y]) ++height[x];
    forest_num--;
  }
  bool same(int x, int y) { return find(x) == find(y); }
  int size(int x) {
    if (parent[x] == x) return m_size[x];
    return size(parent[x] = find(parent[x]));
  }
  int forest() { return forest_num; }
};
