class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        ll n  = nums.size();
         map<ll, vll>mp;
         vll v;
         rep(i,0,n/2){
           ll a = nums[i] , b = nums[n - i - 1];
           ll diff = abs(a - b);
           ll x = max4(a,b,k - a , k - b);
           mp[diff].pb(x);
           v.pb(x); 
         }
         ll ans = nums.size();
         sort(all(v));
         for(auto it : mp){
            ll diff = it.ff;
            ll d = lower_bound(all(v),diff) - v.begin();
            d = 2*d + (n/2-d);
            for(auto val : it.ss){
                if(val < diff)d -= 2;
                else d -= 1;
            }
            ans = min(ans,d);
         }
         return ans;

    }
};
