class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int>m;
        vector<int>arr(k+1,0);
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            int f = min(nums[i],nums[n-i-1]);
            int s = max(nums[i],nums[n-i-1]);
            int mx = max(s,k-f);
            arr[mx]++;
        }
        for(int i=k-1;i>=0;i--){
            arr[i]+=arr[i+1];
        }
        int ans = INT_MAX;
        for(int i=0;i<=k;i++){
            int c = (arr[i]-m[i])+(n/2-arr[i])*2;
            ans=min(ans,c);
        }
        return ans;
    }
};
