class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> v;
        int n = nums.size();
        for(int i = 0 ; i<n/2 ;i++)
        {
            int d = abs(nums[i]-nums[n-i-1]);
            int tem = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[d].push_back(tem);
            v.push_back(tem);
        }
        int mn  =n;
        sort(v.begin(),v.end());
        for(auto it: mp)
        {
            int target = it.first;
            int idx = lower_bound(v.begin(),v.end(),target) - v.begin() ;
            idx = 2 * idx  + (n/2 - idx) ;
            for(auto v:it.second)
            {
                if(v<target)
                idx-=2;
                else
                idx-=1;
            }
            mn = min(mn,idx);
        }
        return mn;

    }
};
