class Solution {
    public int minChanges(int[] nums, int k) {
        int len = nums.length / 2;
        int[] diff = new int[k+2];
        for (int i = 0; i < len; i++) {
            int x1 = Math.abs(nums[i] - nums[nums.length - i - 1]);
            int max1 = Math.max(k - nums[i], nums[i]);
            int max2 = Math.max(k - nums[nums.length-i-1], nums[nums.length-i-1]);
            int x2 = Math.max(max1, max2);
            diff[0]++;
            diff[x1]--;
            diff[x1+1]++;
            diff[x2+1]++;
            diff[k+1]-=2;
        }
        int ans = Integer.MAX_VALUE;
        int sum = 0;
        for (int i = 0; i < diff.length - 1; i++) {
            sum += diff[i];
            ans = Math.min(sum, ans);
        }
        return ans;
    }
}
