class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size ();
        int i = 0, j = n-1;
        vector <int> dp (k+2, 0);
        while (i < j)
        {
            int x = abs (nums[i] - nums[j]);
            int y = max (nums[i], nums[j]);
            y = max (y, k - nums[i]);
            y = max (y, k - nums[j]);
            dp [0]++;
            dp [x]--;
            dp [x+1]++;
            dp [y+1]++;
            ++i, --j;
        }
        int cnt = 0;
        int ans = n;
        for (int i = 0; i <= k; ++i)
        {
            cnt += dp[i];
            ans = min (ans, cnt);
        }
        return ans;
    }
};
