class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int mx = max(*max_element(nums.begin(), nums.end()), k);
        vector<long long> s(mx + 2);

        int i = 0, j = nums.size() - 1;
        while (i < j) {
            int a = nums[i++];
            int b = nums[j--];
            if (a < b) {
                swap(a, b);
            }

            // 加 0 区间
            pair<int, int> zero = {abs(b - a), abs(b - a)};

            // 加 1 区间
            pair<int, int> one1 = {-1, -1};
            int r1 = k - b, l1 = 0 - b;
            if (1ll * r1 * l1 < 0) {
                one1 = {0, max(abs(r1), abs(l1))};
            } else {
                one1 = {min(abs(r1), abs(l1)), max(abs(r1), abs(l1))};
            }
            pair<int, int> one2 = {-1, -1};
            int r2 = a - 0, l2 = a - k;
            if (1ll * r2 * l2 < 0) {
                one2 = {0, max(abs(r2), abs(l2))};
            } else {
                one2 = {min(abs(r2), abs(l2)), max(abs(r2), abs(l2))};
            }

            pair<int, int> one = {min(one1.first, one2.first), max(one1.second, one2.second)};

            // 加 2 区间
            pair<int, int> two = {0, k};

            s[0] += 2; s[k + 1] -= 2;

            if (one.first <= k) {
                if (one.second <= k) {
                    s[one.first] += -1;
                    s[one.second + 1] -= -1;
                } else {
                    s[one.first] += -1;
                    s[k + 1] -= -1;
                    s[k + 1] += 1;
                    s[one.second + 1] -= 1;
                }
            }

            if (zero.first >= one.first and zero.second <= one.second) {
                s[zero.first] += -1;
                s[zero.second + 1] -= -1;
            } else if (zero.first >= two.first and zero.second <= two.second) {
                s[zero.first] += -2;
                s[zero.second + 1] -= -2;
            }
        }

        for (int i = 0; i + 1 < s.size(); i++) {
            s[i + 1] += s[i];
        }

        int ans = 2e9;
        for (int i = 0; i + 1 < s.size(); i++) {
            // cout << s[i] << " ";
            ans = min(1ll * ans, s[i]);
        }

        return ans;
    }
};
