class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int, vector<int>> m;
        vector<int> v;
        for (int i = 0; i < nums.size() / 2; i++) {
            int a = nums[i], b = nums[nums.size() - i - 1];
            int difference = abs(a - b);
            int temp = max({a, b, k - a, k - b});
            m[difference].push_back(temp);
            v.push_back(temp);
        }
        int answer = nums.size();
        sort(v.begin(), v.end());

        for (auto& val : m) {

            int difference = val.first;
            int T = lower_bound(v.begin(), v.end(), difference) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (auto& temp : val.second) {
                if (temp < difference) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            answer = min(answer, T);
        }
        return answer;
    }
};
