class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       vector<int>v(k + 4);

        int n = nums.size();
        for(int i = 0; i < n/2; i++){
            int mini = min(nums[i], nums[n-i-1]);
            int maxi = max(nums[i], nums[n-i-1]);

            int val = max(maxi, k - mini);
            v[0]++;
            v[maxi - mini]--;
            v[maxi - mini + 1] += 1;
            v[val + 1] -= 1;
            v[val + 1] += 2;
            v[k+1] -= 2;
        }

        int sum = 0;
        int res = INT_MAX;
        for(int i = 0; i <= k; i++){
            sum += v[i];
            v[i] = sum;

            res = min(res, v[i]);
        }

        return res;



    }
};
