class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        set<int> st;
        map<int,int> mp;
        vector<int> pref(k+1, 0);

        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-1-i])]++;
            st.insert(abs(nums[i]-nums[n-1-i]));
        }

        for(int i=0;i<n/2;i++){
            int a=min(nums[i], nums[n-1-i]);
            int b=max(nums[i], nums[n-1-i]);
            int d=max(k-a, b);
            pref[d]++;
        }
        for(int i=k-1;i>=0;i--){
            pref[i]+=pref[i+1];
        }
        for(auto it:pref) cout<<it<<" ";
        cout<<endl;


        int ans=INT_MAX;
        for(auto it:st){
            ans=min(ans, n-mp[it]-pref[it]);
        }
        return ans;
    }
};
