class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
    vector<int>v;
      map<int,vector<int>>p;

        int n=nums.size();

        for(int i=0;i<n/2;i++)
        {
            int difference=abs(nums[i]-nums[n-i-1]);
            int out=max(nums[i],nums[n-i-1]);
            out=max(out,k-nums[i]);
            out=max(out,k-nums[n-i-1]);
            v.push_back(out);
            p[difference].push_back(out);
        }

        sort(v.begin(),v.end());
        int nx=n;

        for(auto it=p.begin();it!=p.end();++it)
        {
            int lower=lower_bound(v.begin(),v.end(),it->first)-v.begin();
            lower = lower*2+(n/2-lower);
            vector<int>x=it->second;

            for(int i=0;i<x.size();i++)
            {
                if(x[i]<it->first)
                {
                    lower-=2;
                }
                else
                    lower-=1;
            }

            nx=min(nx,lower);

        }
        return nx;

    }
};
