#include <ext/pb_ds/assoc_container.hpp> 
#include <ext/pb_ds/tree_policy.hpp> 
using namespace __gnu_pbds; 

#define ordered_set tree<pair<int,int>, null_type,less<pair<int,int>>, rb_tree_tag,tree_order_statistics_node_update>

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),i,ma=0,ans=n/2,c=0,req,curr,tot=n/2;
        map<int,vector<int> > b;
        ordered_set a;
        for(i=0;i<n/2;i++)
        {
            if(nums[i]>nums[n-1-i])
                swap(nums[i],nums[n-1-i]);
            b[nums[n-1-i]- nums[i]].push_back(nums[n-1-i]- nums[i]+max(k-nums[n-1-i],nums[i]));
        }
        for(auto x:b)
        {
            // cout<<x.first<<" w ";
            // for(auto z:x.second)
            //     cout<<z<<" ";
            // cout<<endl;
            curr=n/2-x.second.size();
            pair<int,int> y = {x.first,-1};
            req=a.order_of_key(y);
            curr+=req;
            ans=min(ans,curr);
            for(auto z:x.second)
                a.insert({z,c++});
        }
        return ans;
    }
};
