class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mpp;
        vector<int>v;
        int n = nums.size() , j = n-1;
        for(int i=0;i<n/2;i++)
        {
            int tt = abs(nums[i] - nums[j]);
            int tt1 = max(nums[i],nums[j]);
            int tt2 = abs(min(nums[i],nums[j--]) - k);
            tt1 = max(tt1,tt2);
            v.push_back(tt1);
            mpp[tt]++;
        }
        vector<pair<int,int>>mx;
        // for
        for(auto &x:mpp) mx.push_back({x.second,x.first});
        sort(mx.begin(),mx.end(),greater<pair<int,int>>());
        sort(v.begin(),v.end());
        int ans = INT_MAX;
        for(auto &x:v) cout<<x<<" ";
        // cout<<endl;
        // cout<<mx.size()<<endl;
        for(int i=0;i<mx.size();i++)
        {
            int it = lower_bound(v.begin(),v.end(),mx[i].second) - v.begin();
            int add=0;
            // while(v[it]<=mx[i].second) it++;
            add = it;
            // cout<<n/2<<" "<<it<<" "<<mx[i].second<<endl;
            // cout<<add<<" ";
            int temp_ans = n/2 - mx[i].first + add;
            ans=min(ans,temp_ans);
        }
        return ans;
    }
};
