class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>ans;
        map<int,vector<int>> m;
        int i=0,n=nums.size();
        while(i<(n/2)){
            int a=nums[i],b=nums[n-i-1];
            int x=abs(a-b);
            int y=max({a,b,k-a,k-b});
            m[x].push_back(y);
            ans.push_back(y);
            i++;
        }
        sort(ans.begin(),ans.end());
        int res = nums.size();

        for(auto &i:m)
        {
            int diff = i.first;
            int T = lower_bound(ans.begin(),ans.end(),diff)-ans.begin();
            T = T*2+(nums.size()/2-T);
            for(auto &j:i.second){
                if(j<diff)
                    T-=2;
                else
                    T-=1;
            }
            res = min(res,T);
        }
        return res;
    }
};
