class Solution {
public:
    int test(int num,vector<int> nums,int k)
    {
        //判断差值是否可以实现，需要的次数是多少

        int n=nums.size();
        int ans=0;

        for(int i=0;i<n/2;i++)
        {
            int a=nums[i];
            int b=nums[n-i-1];
            int t=num;
            int cha=abs(nums[i]-nums[n-i-1]);
            if(cha!=num) {
                //need xiugai
                if(b+t<=k || a+t<=k){
                    ans++;
                    continue;
                }
                if(b-t<=k && b-t>=0){
                    ans++;
                    continue;
                }
                if(a-t<=k && a-t>=0){
                    ans++;
                    continue;
                }
                if(k<num) return -1;
                ans+=2;
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        //先滚动一遍，得到差值出现的次数列表，再遍历列表，哪个可信就ok
        const int N=10e5+5;
        map<int,int> cnt;

        int n=nums.size();

        for(int i=0;i<n/2;i++)
        {
            int ind=abs(nums[i]-nums[n-i-1]);
            if(cnt.find(ind)==cnt.end()) cnt.insert({ind,1});
            else cnt[ind]++;
        }
        //颠倒key & value
        map<int,int> ::iterator it;
        map<int,int> map1;
        for(it=cnt.begin();it!=cnt.end();it++)
        {
            map1.insert({(-1)*it->second,it->first});
        }
        //调换后，key是差值出现了几次，value是差值的具体值。
        it=map1.begin();
        int res;
        for(;it!=map1.end();it++)
        {
            res = test(it->second,nums,k);
            if(res>=0) break;
        }        
        for(it++;it!=map1.end();it++)
        {
            int res1 = test(it->second,nums,k);
            if(res1>=0) return min(res1,res);
        }
        return res;            


    }
};
