class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = (int)nums.size();
        unordered_map<int, int> mp;
        unordered_set<int> ss;
        for(int i = 0 ; i < n / 2 ; i++) {
            ss.insert(abs(nums[i] - nums[n - i - 1]));
            mp[abs(nums[i] - nums[n - i - 1])]++;
        }
        priority_queue<pair<int, int>> pq;
        for(int i : ss) pq.push({mp[i], i});
        vector<int> d;
        if(pq.top().first == n / 2) return 0;
        for(int i = 0 ; i < 1000 && !pq.empty() ; i++) {
            d.push_back(pq.top().second);
            pq.pop();
        }
        int mn = n / 2;
        for(int i = 0 ; i < (int)d.size() ; i++) {
            int ans = 0;
            for(int j = 0 ; j < n / 2 ; j++) {
                if(abs(nums[j] - nums[n - j - 1]) != d[i]) {
                    int val = nums[j] + d[i];
                    int val1 = nums[j] - d[i];
                    int val2 = nums[n - j - 1] + d[i];
                    int val3 = nums[n - j - 1] - d[i];
                    if((val >= 0 && val <= k) || (val1 >= 0 && val1 <= k) || (val2 >= 0 && val2 <= k) || (val3 >= 0 && val3 <= k)) {
                        ++ans;
                    } else ans += 2;
                }
            }
            mn = min(mn, ans);
        }
        return mn;
    }
};
