class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp; map<int,int> mp; int n=nums.size();
        for(int i=0;i<n/2;i++){
            int num=max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]}); temp.push_back(num);
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        sort(temp.begin(),temp.end());
        int ans1=1e9+7;
        for(int i=0;i<nums.size()/2;i++){
            int dif=abs(nums[i]-nums[n-i-1]);
            int pos=lower_bound(temp.begin(),temp.end(),dif)-temp.begin();
            int ans=temp.size()-pos+(pos)*2;
            ans=ans-mp[dif];
            ans1=min(ans1,ans);
        }
        return ans1;

    }
};
