class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        mp = {}
        n = len(nums)
        for i in range(int(n/2)):
            val = abs(nums[i]-nums[n-i-1])
            if val in mp:
                mp[val]+=1
            else:
                mp[val] = 1

        res = sorted(mp.items(), key=lambda x: (-x[1], x[0]))

        min_count = n
        ind = min(10, len(res))
        for j in range(ind):
            max_val = res[j][0]
            count = 0
            for i in range(int(n/2)):
                val = abs(nums[i]-nums[n-i-1])
                if max_val != val:
                    if nums[i]+max_val<=k or nums[i]-max_val>=0:
                        count+=1
                    elif nums[n-i-1]+max_val<=k or nums[n-i-1]-max_val>=0:
                        count += 1
                    else:
                        count += 2
            min_count = min(min_count, count)
        return min_count
