class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int> vec;
       map<int, vector<int>> mp;


        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int difference = std::abs(a - b);

             int X = std::max({a, b, k - a, k - b});

             mp[difference].push_back(X);
            vec.push_back(X);
        }

        int ans = nums.size();  
        std::sort(vec.begin(), vec.end());

        for (const auto& [difference, values] : mp) {
            int T = std::lower_bound(vec.begin(), vec.end(), difference) - vec.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < difference) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = std::min(ans, T);
        }

        return ans;
    }
};
