class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        for(int i=0;i<n/2;i++){
           mp[abs(nums[i]-nums[n-1-i])]++;
        }
        int ans=INT_MAX;
        vector<int> mx;
        for(int i=0;i<n/2;i++){
            int x=nums[i];
            int y=nums[n-1-i];
            mx.push_back(max({x,y,k-x,k-y}));
        }
        sort(mx.begin(),mx.end());
        for(auto it:mx) cout<<it<<" ";
        cout<<endl;
        for(auto it:mp){
            int x=it.first;
            int c=it.second;
            if(c==n/2) return 0;
            int num=upper_bound(mx.begin(),mx.end(),x-1)-mx.begin();
            if(num>=n/2) num=0;
            if(num<0) num=n/2;
            ans=min(ans,num*2+(n/2-num-c));
            cout<<x<<" "<<c<<" "<<num<<endl;


        }
        return ans;
    }
};
